pub fn day6() {
    println!(
        "{:?}",
        packet_start_detector("")
    )
}

fn packet_start_detector(characters: &str) -> usize {
    let mut string_buffer = String::with_capacity(5);
    let mut start_index = 0;
    let char_vec = characters.chars().enumerate().collect::<Vec<_>>();

    while string_buffer.len() < 4 {
        let slices = &char_vec[start_index..start_index + 4];

        for (_, character) in slices {
            if string_buffer.contains(*character) {
                for (inner_index, inner_val) in string_buffer.chars().enumerate() {
                    if inner_val == *character {
                        start_index = start_index + (inner_index + 1);
                        break;
                    }
                }

                string_buffer.clear();
                break;
            }
            string_buffer.push(*character);
        }
    }

    start_index + 4
}
